import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { authGuard } from './core/guards/auth.guard';
import { Perfil } from './core/services/auth.service'; // ðŸ‘ˆ IMPORTANTE

const routes: Routes = [
{ path: '', redirectTo: '/auth/login', pathMatch: 'full' },

{
path: 'auth',
loadChildren: () =>
      import('./features/auth/auth.module').then(m => m.AuthModule),
  },
  {
    path: 'admin',
    loadChildren: () =>
      import('./features/admin/admin.module').then(m => m.AdminModule),
    canActivate: [authGuard],
    data: { roles: [Perfil.ADMIN] } // âœ… usando o enum corretamente
  },
  {
    path: 'cliente',
    loadChildren: () =>
      import('./features/cliente/cliente.module').then(m => m.ClienteModule),
    canActivate: [authGuard],
    data: { roles: [Perfil.CLIENTE] } // âœ… idem
  },
  { path: '**', redirectTo: '/auth/login' }
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule {}
