import { Component, OnInit } from '@angular/core';
// Correção: Adicionado 'tap'
import { Observable, BehaviorSubject, switchMap, catchError, of, tap } from 'rxjs';
import { AgendamentoService } from '../../../admin/services/agendamento.service';
import { AgendamentoResponse } from '../../../admin/models/agendamento.model';
import { PetService } from '../../../admin/services/pet.service';
import { ServicoService } from '../../../admin/services/servico.service';
import { Pet } from '../../../admin/models/pet.model';
import { Servico } from '../../../admin/models/servico.model';

@Component({
selector: 'app-meus-agendamentos',
// Correção: Verificado o caminho do templateUrl
templateUrl: './meus-agendamentos.component.html',
styleUrls: ['./meus-agendamentos.component.css']
})
export class MeusAgendamentosComponent implements OnInit {

// Agendamentos existentes
// Correção: Adicionado '!'
public agendamentos$!: Observable<AgendamentoResponse[]>;
public isLoadingAgendamentos = true;
public errorAgendamentos: string | null = null;
private refreshAgendamentos = new BehaviorSubject<void>(undefined); // Para recarregar a lista

// Dados para o modal
// Correção: Adicionado '!'
public petsCliente$!: Observable<Pet[]>;
// Correção: Adicionado '!'
public servicosDisponiveis$!: Observable<Servico[]>;
public isLoadingModalData = false;

// Estado do Modal
public isModalOpen = false;

constructor(
    private agendamentoService: AgendamentoService,
    private petService: PetService, // Para buscar os pets do cliente
    private servicoService: ServicoService // Para buscar os serviços disponíveis
  ) { }

  ngOnInit(): void {
    // Carrega a lista de agendamentos e reage a atualizações
    this.agendamentos$ = this.refreshAgendamentos.pipe(
      switchMap(() => {
        this.isLoadingAgendamentos = true;
        this.errorAgendamentos = null;
        return this.agendamentoService.getAgendamentos().pipe(
          catchError((err) => {
            this.isLoadingAgendamentos = false;
            this.errorAgendamentos = "Falha ao carregar seus agendamentos.";
            console.error(err);
            return of([]); // Retorna array vazio em caso de erro
          })
        );
      }),
      tap(() => this.isLoadingAgendamentos = false) // Desativa o loading após sucesso
    );

    // Carrega dados necessários para o modal (executa apenas uma vez)
    this.loadModalData();
  }

  /**
   * UC05 - Carrega a lista de agendamentos do cliente logado.
   */
  loadAgendamentos(): void {
    this.refreshAgendamentos.next(); // Dispara o observable para recarregar
  }

  /**
   * Carrega os dados necessários para o modal de novo agendamento
   * (pets do cliente e serviços disponíveis).
   */
  loadModalData(): void {
    this.isLoadingModalData = true;
    // Busca os pets do cliente logado
    this.petsCliente$ = this.petService.getMeusPets().pipe(
      catchError(err => {
        console.error("Erro ao buscar pets:", err);
        return of([]);
      })
    );
    // Busca todos os serviços (o cliente selecionará quais quer)
    this.servicosDisponiveis$ = this.servicoService.getAllServicos().pipe(
      catchError(err => {
        console.error("Erro ao buscar serviços:", err);
        return of([]);
      })
    );

    // Simplificação: Assume que ambos carregam rapidamente.
    // Em um cenário real, poderia usar forkJoin para esperar ambos.
    this.petsCliente$.subscribe(() => {}); // Apenas para iniciar
    this.servicosDisponiveis$.subscribe(() => this.isLoadingModalData = false); // Termina o loading
  }

  /**
   * CT03.4 - Cancelar Agendamento
   */
  onCancel(id: number, data: string): void {
    // Usar a data formatada pode ser melhor
    const dataFormatada = new Date(data).toLocaleDateString('pt-BR', { day: '2-digit', month: '2-digit', year: 'numeric', hour: '2-digit', minute: '2-digit'});
    if (confirm(`Tem certeza que deseja cancelar o agendamento do dia ${dataFormatada}?`)) {
      this.agendamentoService.cancelarAgendamento(id).subscribe({
        next: () => {
          this.loadAgendamentos(); // Recarrega a lista
        },
        error: (err) => {
          alert("Erro ao cancelar agendamento: " + (err.error?.erro || err.message));
        }
      });
    }
  }

  /**
   * UC05 - (Fluxo 1) Inicia o fluxo de "Novo Agendamento"
   */
  openAgendamentoModal(): void {
    // Recarrega os dados caso algo tenha mudado (ex: admin cadastrou novo pet)
    this.loadModalData();
    this.isModalOpen = true;
  }

  closeAgendamentoModal(): void {
    this.isModalOpen = false;
  }

  /**
   * Chamado quando o modal de agendamento emite 'saveSuccess'.
   */
  handleSaveSuccess(): void {
    this.isModalOpen = false;
    this.loadAgendamentos(); // Recarrega a lista após novo agendamento
  }

  // Helper para formatar data no template
  formatDate(dateString: string): string {
     return new Date(dateString).toLocaleDateString('pt-BR', {day: '2-digit', month: '2-digit', year: 'numeric', hour: '2-digit', minute: '2-digit'});
  }
}
