import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators, AbstractControl, ValidationErrors } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ClienteService } from '../../../services/cliente.service';
import { PetService } from '../../../services/pet.service';
import { Cliente } from '../../../models/cliente.model';
import { Pet } from '../../../models/pet.model';

@Component({
selector: 'app-cliente-form',
templateUrl: './cliente-form.component.html',
styleUrls: ['./cliente-form.component.css']
})
export class ClienteFormComponent implements OnInit {
clienteForm: FormGroup;
isEditMode = false;
isLoading = false;
errorMessage: string | null = null;

public clienteId: number | null = null;
public cliente: Cliente | null = null; // Para listar os pets

public isPetModalOpen = false;
public currentPetToEdit: Pet | null = null;

constructor(
    private fb: FormBuilder,
    private clienteService: ClienteService,
    private petService: PetService,
    private router: Router,
    private route: ActivatedRoute
  ) {
    this.clienteForm = this.fb.group({
      nome: ['', [Validators.required, Validators.maxLength(100)]],
      email: ['', [Validators.required, Validators.email, Validators.maxLength(100)]],
      senha: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(20)]],
      cpf: ['', [Validators.required, this.cpfValidator]],
      telefone: ['', [Validators.required, this.telefoneValidator]],
      endereco: ['', [Validators.maxLength(200)]]
    });
  }

  ngOnInit(): void {
    const idParam = this.route.snapshot.paramMap.get('id');
    if (idParam) {
      this.isEditMode = true;
      this.clienteId = +idParam;
      this.loadClienteData();

      // senha não obrigatória em edição
      this.clienteForm.get('senha')?.clearValidators();
      this.clienteForm.get('senha')?.updateValueAndValidity();
    }
  }

  loadClienteData(): void {
    if (!this.clienteId) return;
    this.isLoading = true;

    this.clienteService.getClienteById(this.clienteId).subscribe({
      next: (cliente) => {
        this.isLoading = false;
        this.cliente = cliente;
        this.clienteForm.patchValue({
          nome: cliente.nome,
          email: cliente.email,
          cpf: cliente.cpf,
          telefone: cliente.telefone,
          endereco: cliente.endereco
        });
      },
      error: () => {
        this.isLoading = false;
        this.errorMessage = "Cliente não encontrado.";
        this.router.navigate(['/admin/clientes']);
      }
    });
  }

  onSubmit(): void {
    if (this.clienteForm.invalid) {
      this.errorMessage = "Por favor, corrija os erros no formulário.";
      return;
    }

    this.isLoading = true;
    this.errorMessage = null;
    const formValue = this.clienteForm.value;

    const saveOperation = this.isEditMode
      ? this.clienteService.updateCliente(this.clienteId!, {
          nome: formValue.nome,
          cpf: formValue.cpf.replace(/\D/g, ''), // salva somente números
          telefone: formValue.telefone.replace(/\D/g, ''), // salva somente números
          endereco: formValue.endereco
        })
      : this.clienteService.createCliente({
          ...formValue,
          cpf: formValue.cpf.replace(/\D/g, ''),
          telefone: formValue.telefone.replace(/\D/g, '')
        });

    saveOperation.subscribe({
      next: () => {
        this.isLoading = false;
        this.router.navigate(['/admin/clientes']);
      },
      error: (err) => {
        this.isLoading = false;
        this.errorMessage = err.error?.erro || "Erro ao salvar cliente. Verifique o CPF e E-mail.";
      }
    });
  }

  // ===== Validação de CPF =====
  cpfValidator(control: AbstractControl): ValidationErrors | null {
    const cpf = control.value.replace(/\D/g, '');
    if (!cpf || cpf.length !== 11) {
      return { invalidCpf: 'CPF inválido. Deve conter 11 números.' };
    }
    return null;
  }

  // ===== Validação de Telefone =====
  telefoneValidator(control: AbstractControl): ValidationErrors | null {
    const telefone = control.value.replace(/\D/g, '');
    if (!telefone || (telefone.length !== 10 && telefone.length !== 11)) {
      return { invalidTelefone: 'Telefone inválido. Deve conter 10 ou 11 números.' };
    }
    return null;
  }

  // ===== Formatação visual CPF =====
  formatCPF(event: Event) {
    const input = event.target as HTMLInputElement;
    let value = input.value.replace(/\D/g, '').slice(0, 11);
    if (value.length > 3) value = value.replace(/^(\d{3})(\d)/, '$1.$2');
    if (value.length > 6) value = value.replace(/^(\d{3})\.(\d{3})(\d)/, '$1.$2.$3');
    if (value.length > 9) value = value.replace(/^(\d{3})\.(\d{3})\.(\d{3})(\d)/, '$1.$2.$3-$4');
    input.value = value;
    this.clienteForm.get('cpf')?.setValue(value, { emitEvent: false });
  }

  // ===== Formatação visual Telefone =====
  formatTelefone(event: Event) {
    const input = event.target as HTMLInputElement;
    let value = input.value.replace(/\D/g, '').slice(0, 11);
    if (value.length > 2) value = value.replace(/^(\d{2})(\d)/, '($1)$2');
    if (value.length > 6) value = value.replace(/^(\(\d{2}\))(\d{5})(\d)/, '$1$2-$3');
    input.value = value;
    this.clienteForm.get('telefone')?.setValue(value, { emitEvent: false });
  }

  openPetModal(pet: Pet | null = null): void {
    this.currentPetToEdit = pet;
    this.isPetModalOpen = true;
  }

  closePetModal(): void {
    this.isPetModalOpen = false;
    this.currentPetToEdit = null;
  }

  handlePetSave(): void {
    this.loadClienteData(); // Atualiza a lista de pets
  }

  onDeletePet(pet: Pet): void {
    if (confirm(`Tem certeza que deseja excluir o pet "${pet.nome}"?`)) {
      this.petService.deletePet(pet.id).subscribe({
        next: () => this.loadClienteData(),
        error: () => alert("Erro ao excluir pet.")
      });
    }
  }

  goBack(): void {
    this.router.navigate(['/admin/clientes']);
  }
}
<div class="form-container card">
    <div class="form-header">
        <h1>{{ isEditMode ? 'Editar Cliente' : 'Cadastrar Novo Cliente' }}</h1>
        <button type="button" class="btn-back" (click)="goBack()">Voltar</button>
    </div>

    <form [formGroup]="clienteForm" (ngSubmit)="onSubmit()" class="form-section">
        <div class="form-row">
            <div class="form-group">
                <label>Nome</label>
                <input type="text" formControlName="nome" maxlength="100" placeholder="Digite o nome">
            </div>

            <div class="form-group">
                <label>E-mail</label>
                <input type="email" formControlName="email" maxlength="100" placeholder="Digite o e-mail">
            </div>
        </div>

        <div class="form-row" *ngIf="!isEditMode">
            <div class="form-group">
                <label>Senha</label>
                <input type="password" formControlName="senha" maxlength="20" placeholder="Digite a senha">
            </div>
        </div>

        <div class="form-row">
            <div class="form-group">
                <label>CPF</label>
                <input type="text"
                       formControlName="cpf"
                       maxlength="14"
                       placeholder="000.000.000-00"
                       (input)="formatCPF($event)">
            </div>

            <div class="form-group">
                <label>Telefone</label>
                <input type="text"
                       formControlName="telefone"
                       maxlength="15"
                       placeholder="(00) 00000-0000"
                       (input)="formatTelefone($event)">
            </div>
        </div>

        <div class="form-group">
            <label>Endereço</label>
            <input type="text" formControlName="endereco" maxlength="200" placeholder="Digite o endereço">
        </div>

        <div *ngIf="errorMessage" class="error-message">{{ errorMessage }}</div>

        <div class="form-actions">
            <button type="submit" [disabled]="isLoading" class="btn-primary">
                {{ isLoading ? 'Salvando...' : 'Salvar Cliente' }}
            </button>
            <button type="button" class="btn-secondary" (click)="goBack()">Voltar</button>
        </div>
    </form>

    <div class="pet-section" *ngIf="cliente">
        <div class="page-header">
            <h2>Pets do Cliente</h2>
        </div>
        <ul *ngIf="cliente.pets.length > 0; else noPets" class="pet-list">
            <li *ngFor="let pet of cliente.pets" class="pet-item">
                <span>{{ pet.nome }} - {{ pet.especie }}</span>
                <div class="pet-actions">
                    <button (click)="openPetModal(pet)" class="btn-edit">Editar</button>
                    <button (click)="onDeletePet(pet)" class="btn-delete">Excluir</button>
                </div>
            </li>
        </ul>
        <ng-template #noPets>
            <div class="no-data">Nenhum pet cadastrado.</div>
        </ng-template>

        <button class="btn-add" (click)="openPetModal()">Adicionar Pet</button>
    </div>

    <app-pet-form-modal
            *ngIf="isPetModalOpen"
            [pet]="currentPetToEdit"
            [clienteId]="clienteId"
            (saveSuccess)="handlePetSave()"
            (closeModal)="closePetModal()">
    </app-pet-form-modal>
</div>
