import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { Produto } from '../../../models/produto.model';
import { ProdutoService } from '../../../services/produto.service';

@Component({
selector: 'app-produto-list',
templateUrl: './produto-list.component.html',
// Reutilizando o CSS da lista de clientes
styleUrls: ['../../clientes/cliente-list/cliente-list.component.css']
})
export class ProdutoListComponent implements OnInit {

// Correção: Adicionado '!'
public produtos$!: Observable<Produto[]>;
public isLoading = true;
public error: string | null = null;

constructor(
    private produtoService: ProdutoService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadProdutos();
  }

  loadProdutos(): void {
    this.isLoading = true;
    this.error = null;
    this.produtos$ = this.produtoService.getAllProdutos();

    this.produtos$.subscribe({
      next: () => this.isLoading = false,
      error: (err) => {
        this.isLoading = false;
        this.error = "Falha ao carregar produtos. Tente novamente.";
        console.error(err);
      }
    });
  }

  navigateToAdd(): void {
    this.router.navigate(['/admin/produtos/novo']);
  }

  navigateToEdit(id: number): void {
    this.router.navigate(['/admin/produtos/editar', id]);
  }

  onDelete(id: number, nome: string): void {
    // Pede confirmação
    if (confirm(`Tem certeza que deseja excluir o produto "${nome}"?`)) {
      this.produtoService.deleteProduto(id).subscribe({
        next: () => {
          this.loadProdutos(); // Recarrega a lista
        },
        error: (err) => {
          // Fluxo de Exceção: Item com Histórico
          this.error = err.error?.erro || "Falha ao excluir produto.";
          console.error(err);
        }
      });
    }
  }
}

<div class="list-container">
    <header class="page-header">
        <h1>Gerenciar Produtos</h1>
        <button class="btn-primary btn-add" (click)="navigateToAdd()">
            + Novo Produto
        </button>
    </header>

    <div *ngIf="isLoading" class="loading-indicator">
        Carregando...
    </div>

    <div *ngIf="error" class="error-message">
        {{ error }}
    </div>

    <!--
      Correção:
      Removido o 'else noDataTemplate' e o <ng-template>
      para corrigir o erro de escopo da variável 'produtos' e a referência do template.
    -->
    <div *ngIf="(produtos$ | async) as produtos">
        <!--
          Correção: Removido '?' de produtos.length,
          pois 'produtos' está garantido pelo *ngIf pai.
        -->
        <table class="data-table" *ngIf="!isLoading && produtos.length > 0">
            <thead>
            <tr>
                <th>Nome</th>
                <th>Estoque</th>
                <th>Preço de Custo</th>
                <th>Preço de Venda</th>
                <th>Ações</th>
            </tr>
            </thead>
            <tbody>
            <tr *ngFor="let produto of produtos">
                <td>{{ produto.nome }}</td>
                <td>{{ produto.qtdEstoque }}</td>
                <td>{{ produto.precoCusto | currency:'BRL' }}</td>
                <td>{{ produto.precoVenda | currency:'BRL' }}</td>
                <td class="action-buttons">
                    <button class="btn-edit" (click)="navigateToEdit(produto.id)">
                        Editar
                    </button>
                    <button class="btn-delete" (click)="onDelete(produto.id, produto.nome)">
                        Excluir
                    </button>
                </td>
            </tr>
            </tbody>
        </table>

        <!--
          Correção: Mensagem "sem dados" agora usa a variável 'produtos'
          do escopo correto.
        -->
        <div *ngIf="!isLoading && produtos.length === 0" class="no-data">
            Nenhum produto cadastrado ainda.
        </div>
    </div>
</div>
