import { Component, EventEmitter, Input, OnChanges, Output, SimpleChanges } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PetService } from '../../services/pet.service';
import { Pet, PetDTO } from '../../models/pet.model';

@Component({
selector: 'app-pet-form-modal',
templateUrl: './pet-form-modal.component.html',
styleUrls: ['./pet-form-modal.component.css']
})
export class PetFormModalComponent implements OnChanges {
// --- Entradas e Saídas ---
@Input() clienteId: number | null = null;
@Input() pet: Pet | null = null;
@Output() closeModal = new EventEmitter<void>();
@Output() saveSuccess = new EventEmitter<void>();

// --- Estado ---
petForm: FormGroup;
isEditMode = false;
isLoading = false;
errorMessage: string | null = null;

constructor(
    private fb: FormBuilder,
    private petService: PetService
  ) {
    this.petForm = this.fb.group({
      nome: ['', Validators.required],
      especie: ['', Validators.required],
      raca: ['', Validators.required]
    });
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['pet'] && this.pet) {
      // Modo edição
      this.isEditMode = true;
      this.petForm.patchValue({
        nome: this.pet.nome,
        especie: this.pet.especie,
        raca: this.pet.raca
      });
    } else {
      // Modo criação
      this.isEditMode = false;
      this.petForm.reset();
    }
  }

  submitForm(): void {
    if (this.petForm.invalid || !this.clienteId) {
      this.errorMessage = "Todos os campos são obrigatórios.";
      return;
    }

    this.isLoading = true;
    this.errorMessage = null;

    const petData: PetDTO = {
      ...this.petForm.value,
      clienteId: this.clienteId
    };

    const saveOperation = this.isEditMode && this.pet
      ? this.petService.updatePet(this.pet.id, petData)
      : this.petService.createPet(petData);

    saveOperation.subscribe({
      next: () => {
        this.isLoading = false;
        this.saveSuccess.emit();
        this.close(); // Fechar modal corretamente
      },
      error: (err) => {
        this.isLoading = false;
        this.errorMessage = err.error?.erro || "Erro ao salvar o pet.";
      }
    });
  }

  /**
   * Fecha o modal e reseta o formulário
   */
  close(): void {
    this.petForm.reset();
    this.errorMessage = null;
    this.isEditMode = false;
    this.closeModal.emit();
  }

  /**
   * Chamado ao clicar no overlay
   */
  onOverlayClick(event: MouseEvent): void {
    event.stopPropagation();
    this.close();
  }
}
<div class="modal-overlay" (click)="onClose()"></div>

<div class="modal-container card">
    <header class="modal-header">
        <h2>{{ isEditMode ? 'Editar Pet' : 'Adicionar Novo Pet' }}</h2>
        <button type="button" class="btn-close" (click)="onClose()">×</button>
    </header>

    <form [formGroup]="petForm" (ngSubmit)="submitForm()" class="form-section">
        <div class="modal-body">
            <div class="form-row">
                <div class="form-group">
                    <label for="pet-nome">Nome do Pet</label>
                    <input
                            id="pet-nome"
                            type="text"
                            formControlName="nome"
                            class="form-control"
                            placeholder="Ex: Rex"
                    />
                    <div *ngIf="petForm.get('nome')?.touched && petForm.get('nome')?.invalid" class="error-message">
                        Nome é obrigatório.
                    </div>
                </div>

                <div class="form-group">
                    <label for="pet-especie">Espécie</label>
                    <input
                            id="pet-especie"
                            type="text"
                            formControlName="especie"
                            class="form-control"
                            placeholder="Ex: Cachorro, Gato"
                    />
                    <div *ngIf="petForm.get('especie')?.touched && petForm.get('especie')?.invalid" class="error-message">
                        Espécie é obrigatória.
                    </div>
                </div>

                <div class="form-group">
                    <label for="pet-raca">Raça</label>
                    <input
                            id="pet-raca"
                            type="text"
                            formControlName="raca"
                            class="form-control"
                            placeholder="Ex: SRD, Poodle"
                    />
                    <div *ngIf="petForm.get('raca')?.touched && petForm.get('raca')?.invalid" class="error-message">
                        Raça é obrigatória.
                    </div>
                </div>
            </div>

            <div *ngIf="errorMessage" class="error-message">{{ errorMessage }}</div>
        </div>

        <footer class="modal-footer form-actions">
            <button type="button" class="btn-secondary" (click)="onClose()">Cancelar</button>
            <button type="submit" class="btn-primary" [disabled]="isLoading || petForm.invalid">
                {{ isLoading ? 'Salvando...' : 'Salvar Pet' }}
            </button>
        </footer>
    </form>
</div>
