import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { Cliente } from '../../../models/cliente.model';
import { ClienteService } from '../../../services/cliente.service';

@Component({
selector: 'app-cliente-list',
templateUrl: './cliente-list.component.html',
styleUrls: ['./cliente-list.component.css']
})
export class ClienteListComponent implements OnInit {

// Corre√ß√£o: Adicionado '!' (definite assignment assertion)
public clientes$!: Observable<Cliente[]>;
public isLoading = true;
public error: string | null = null;

constructor(
    private clienteService: ClienteService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadClientes();
  }

  loadClientes(): void {
    this.isLoading = true;
    this.error = null;
    this.clientes$ = this.clienteService.getAllClientes();
    
    this.clientes$.subscribe({
      next: () => this.isLoading = false,
      error: (err) => {
        this.isLoading = false;
        this.error = "Falha ao carregar clientes. Tente novamente.";
        console.error(err);
      }
    });
  }

  /**
   * Navega para o formul√°rio de cria√ß√£o.
   * UC02 - Fluxo "Adicionar um Cliente"
   */
  navigateToAdd(): void {
    this.router.navigate(['/admin/clientes/novo']);
  }

  /**
   * Navega para o formul√°rio de edi√ß√£o.
   * UC02 - Fluxo "Gerenciar um Cliente Existente"
   */
  navigateToEdit(id: number): void {
    this.router.navigate(['/admin/clientes/editar', id]);
  }

  /**
   * Exclui um cliente.
   * UC02 - Fluxo "Excluir Cliente"
   */
  onDelete(id: number, nome: string): void {
    // Fluxo UC02 [136]: Pede confirma√ß√£o
    if (confirm(`Tem certeza que deseja excluir o cliente "${nome}"?`)) {
      this.clienteService.deleteCliente(id).subscribe({
        next: () => {
          // Recarrega a lista ap√≥s a exclus√£o
          this.loadClientes();
          // (Poderia ter um toast de sucesso aqui)
        },
        error: (err) => {
          // Fluxo UC02 [138]: Exclus√£o com Hist√≥rico (tratado no back-end)
          this.error = err.error?.erro || "Falha ao excluir cliente. Verifique se ele possui agendamentos.";
          console.error(err);
        }
      });
    }
  }

  // =======================================================
  //         üëá FUN√á√ïES DE FORMATA√á√ÉO ADICIONADAS üëá
  // =======================================================

  /**
   * Formata um CPF no padr√£o 000.000.000-00
   * @param cpf O CPF (apenas d√≠gitos)
   */
  formatarCPF(cpf: string): string {
    if (!cpf) return '';

    let valorFormatado = cpf.replace(/\D/g, ''); // Remove n√£o-d√≠gitos

    if (valorFormatado.length === 11) {
      return valorFormatado.replace(
        /(\d{3})(\d{3})(\d{3})(\d{2})/,
        '$1.$2.$3-$4'
      );
    }

    return cpf; // Retorna original se n√£o tiver 11 d√≠gitos
  }

  /**
   * Formata um Telefone no padr√£o (00) 0000-0000 ou (00) 00000-0000
   * @param telefone O telefone (apenas d√≠gitos)
   */
  formatarTelefone(telefone: string): string {
    if (!telefone) return '';

    let valorFormatado = telefone.replace(/\D/g, ''); // Remove n√£o-d√≠gitos

    if (valorFormatado.length === 10) {
      return valorFormatado.replace(/(\d{2})(\d{4})(\d{4})/, '($1) $2-$3');
    }
    else if (valorFormatado.length === 11) {
      return valorFormatado.replace(/(\d{2})(\d{5})(\d{4})/, '($1) $2-$3');
    }

    return telefone; // Retorna original se o tamanho for inv√°lido
  }
}
