import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { Observable, tap } from 'rxjs';
import { LoginResponse } from '../models/auth.models';

const AUTH_TOKEN_KEY = 'petflow_auth_token';
const USER_ROLE_KEY = 'petflow_user_role';
const USER_NAME_KEY = 'petflow_user_name';

@Injectable({
providedIn: 'root'
})
export class AuthService {

private apiUrl = 'http://localhost:8080/api/auth'; // ajuste para sua API

constructor(private router: Router, private http: HttpClient) {}

  /**
   * Faz login real, validando email e senha no backend.
   * O backend deve retornar token JWT, role e nome do usuário.
   */
  login(email: string, senha: string): Observable<LoginResponse> {
    return this.http.post<LoginResponse>(`${this.apiUrl}/login`, { email, senha })
      .pipe(
        tap(response => {
          this.setSession(response);
          // Redireciona conforme perfil
          if (response.userRole === 'ROLE_ADMIN') {
            this.router.navigate(['/admin/clientes']);
          } else if (response.userRole === 'ROLE_CLIENTE') {
            this.router.navigate(['/cliente/home']); // ajuste para rota do cliente
          }
        })
      );
  }

  /** Armazena token, role e nome no localStorage */
  private setSession(authResponse: LoginResponse): void {
    localStorage.setItem(AUTH_TOKEN_KEY, authResponse.token ?? '');
    localStorage.setItem(USER_ROLE_KEY, authResponse.userRole ?? '');
    localStorage.setItem(USER_NAME_KEY, authResponse.userName ?? '');
  }

  /** Logout */
  logout(): void {
    this.clearSession();
    this.router.navigate(['/auth/login']);
  }

  private clearSession(): void {
    localStorage.removeItem(AUTH_TOKEN_KEY);
    localStorage.removeItem(USER_ROLE_KEY);
    localStorage.removeItem(USER_NAME_KEY);
  }

  getToken(): string | null {
    return localStorage.getItem(AUTH_TOKEN_KEY);
  }

  isAuthenticated(): boolean {
    return !!this.getToken();
  }

  getUserRole(): string | null {
    return localStorage.getItem(USER_ROLE_KEY);
  }

  getUserName(): string | null {
    return localStorage.getItem(USER_NAME_KEY);
  }
}
