import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { Servico } from '../../../models/servico.model';
import { ServicoService } from '../../../services/servico.service';

@Component({
selector: 'app-servico-list',
templateUrl: './servico-list.component.html',
// Reutilizaremos o CSS do cliente-list, pois é idêntico
styleUrls: ['../../clientes/cliente-list/cliente-list.component.css']
})
export class ServicoListComponent implements OnInit {

// Correção: Adicionado '!'
public servicos$!: Observable<Servico[]>;
public isLoading = true;
public error: string | null = null;

constructor(
    private servicoService: ServicoService,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.loadServicos();
  }

  loadServicos(): void {
    this.isLoading = true;
    this.error = null;
    this.servicos$ = this.servicoService.getAllServicos();

    this.servicos$.subscribe({
      next: () => this.isLoading = false,
      error: (err) => {
        this.isLoading = false;
        this.error = "Falha ao carregar serviços. Tente novamente.";
        console.error(err);
      }
    });
  }

  navigateToAdd(): void {
    this.router.navigate(['/admin/servicos/novo']);
  }

  navigateToEdit(id: number): void {
    this.router.navigate(['/admin/servicos/editar', id]);
  }

  onDelete(id: number, nome: string): void {
    // Pede confirmação
    if (confirm(`Tem certeza que deseja excluir o serviço "${nome}"?`)) {
      this.servicoService.deleteServico(id).subscribe({
        next: () => {
          this.loadServicos(); // Recarrega a lista
        },
        error: (err) => {
          // Correção: Removido comentário inválido 'cite_start'
          // Fluxo de Exceção: Item com Histórico
          this.error = err.error?.erro || "Falha ao excluir. Verifique se o serviço está vinculado a agendamentos.";
          console.error(err);
        }
      });
    }
  }
}

<div class="list-container">
    <!-- Cabeçalho da Página -->
    <header class="page-header">
        <h1>Gerenciar Serviços</h1>
        <button class="btn-primary btn-add" (click)="navigateToAdd()">
            + Novo Serviço
        </button>
    </header>

    <!-- Estado de Loading -->
    <div *ngIf="isLoading" class="loading-indicator">
        Carregando...
    </div>

    <!-- Estado de Erro -->
    <div *ngIf="error" class="error-message">
        {{ error }}
    </div>

    <!-- Tabela de Dados (UC04) -->
    <!--
      Lógica do *ngIf já corrigida (sem 'else')
    -->
    <div *ngIf="(servicos$ | async) as servicos">
        <table class="data-table" *ngIf="!isLoading && servicos.length > 0">
            <thead>
            <tr>
                <th>Nome</th>
                <th>Descrição</th>
                <th>Preço</th>
                <th>Ações</th>
            </tr>
            </thead>
            <tbody>
            <tr *ngFor="let servico of servicos">
                <td>{{ servico.nome }}</td>
                <!--
                  --- CORREÇÃO FINAL (Erro TS2532) ---
                  Ambas as partes (antes do slice e a checagem do length)
                  precisam ser protegidas contra 'null' ou 'undefined'.
                  Trocado 'servico.descricao?.length' por '(servico.descricao || '').length'.
                -->
                <td>{{ (servico.descricao || '') | slice:0:100 }}{{ (servico.descricao || '').length > 100 ? '...' : '' }}</td>
                <td>{{ servico.preco | currency:'BRL' }}</td>
                <td class="action-buttons">
                    <button class="btn-edit" (click)="navigateToEdit(servico.id)">
                        Editar
                    </button>
                    <button class="btn-delete" (click)="onDelete(servico.id, servico.nome)">
                        Excluir
                    </button>
                </td>
            </tr>
            </tbody>
        </table>

        <div *ngIf="!isLoading && servicos.length === 0" class="no-data">
            Nenhum serviço cadastrado ainda.
        </div>
    </div>
</div>
