import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { Observable, of } from 'rxjs';
import { LoginResponse } from '../models/auth.models';

const AUTH_TOKEN_KEY = 'petflow_auth_token';
const USER_ROLE_KEY = 'petflow_user_role';
const USER_NAME_KEY = 'petflow_user_name';

@Injectable({
providedIn: 'root'
})
export class AuthService {

constructor(private router: Router) {}

  /**
   * Simula login para testes.
   * Pode escolher entre ADMIN e CLIENTE.
   */
  login(email: string, senha: string, role: 'ROLE_ADMIN' | 'ROLE_CLIENTE' = 'ROLE_CLIENTE'): Observable<LoginResponse> {
    console.log('ðŸ”“ [MODO TESTE] Login simulado:', email, 'Role:', role);

    const fakeResponse: LoginResponse = {
      token: 'fake-jwt-token',
      userRole: role,
      userName: role === 'ROLE_ADMIN' ? 'Admin Teste' : 'Cliente Teste',
      expiresIn: 999999
    };

    this.setSession(fakeResponse);
    return of(fakeResponse);
  }

  /**
   * Alterna o perfil do usuÃ¡rio para testes.
   * Ãštil para trocar de CLIENTE para ADMIN e vice-versa.
   */
  switchRole(): void {
    const currentRole = this.getUserRole();
    const newRole = currentRole === 'ROLE_ADMIN' ? 'ROLE_CLIENTE' : 'ROLE_ADMIN';
    const newName = newRole === 'ROLE_ADMIN' ? 'Admin Teste' : 'Cliente Teste';

    const fakeResponse: LoginResponse = {
      token: 'fake-jwt-token',
      userRole: newRole,
      userName: newName,
      expiresIn: 999999
    };

    this.setSession(fakeResponse);
    console.log(`ðŸ”„ Perfil alterado para: ${newRole}`);
  }

  private setSession(authResponse: LoginResponse): void {
    localStorage.setItem(AUTH_TOKEN_KEY, authResponse.token ?? '');
    localStorage.setItem(USER_ROLE_KEY, authResponse.userRole ?? '');
    localStorage.setItem(USER_NAME_KEY, authResponse.userName ?? '');
  }

  logout(): void {
    this.clearSession();
    this.router.navigate(['/auth/login']);
  }

  private clearSession(): void {
    localStorage.removeItem(AUTH_TOKEN_KEY);
    localStorage.removeItem(USER_ROLE_KEY);
    localStorage.removeItem(USER_NAME_KEY);
  }

  getToken(): string | null {
    return localStorage.getItem(AUTH_TOKEN_KEY);
  }

  isAuthenticated(): boolean {
    return true; // sempre verdadeiro no modo teste
  }

  getUserRole(): string | null {
    return localStorage.getItem(USER_ROLE_KEY) ?? 'ROLE_CLIENTE';
  }

  getUserName(): string | null {
    return localStorage.getItem(USER_NAME_KEY) ?? 'Cliente Teste';
  }
}
